import _ from 'lodash';
import PropTypes from 'prop-types';
import React, { Component } from 'react';
import LazyLoad from 'react-lazyload';

const posterPlaceholder = 'data:image/png;base64,';

function findPoster(images) {
  return _.find(images, { coverType: 'poster' });
}

function getPosterUrl(poster, size) {
  if (poster) {
    if (poster.url.contains('lastWrite=') || (/^https?:/).test(poster.url)) {
      // Remove protocol
      let url = poster.url.replace(/^https?:/, '');
      url = url.replace('poster.jpg', `poster-${size}.jpg`);

      return url;
    }
  }
}

class ArtistPoster extends Component {

  //
  // Lifecycle

  constructor(props, context) {
    super(props, context);

    const pixelRatio = Math.floor(window.devicePixelRatio);

    const {
      images,
      size
    } = props;

    const poster = findPoster(images);

    this.state = {
      pixelRatio,
      poster,
      posterUrl: getPosterUrl(poster, pixelRatio * size),
      isLoaded: false,
      hasError: false
    };
  }

  componentDidUpdate(prevProps) {
    const {
      images,
      size
    } = this.props;

    const {
      poster,
      pixelRatio
    } = this.state;

    const nextPoster = findPoster(images);

    if (nextPoster && (!poster || nextPoster.url !== poster.url)) {
      this.setState({
        poster: nextPoster,
        posterUrl: getPosterUrl(nextPoster, pixelRatio * size),
        hasError: false
        // Don't reset isLoaded, as we want to immediately try to
        // show the new image, whether an image was shown previously
        // or the placeholder was shown.
      });
    }

    // The poster could not be loaded..
    if (!nextPoster && (this.props !== prevProps)) {
      this.setState({
        poster: undefined,
        posterUrl: posterPlaceholder,
        hasError: true
      });
    }
  }

  //
  // Listeners

  onError = () => {
    this.setState({ hasError: true });
  }

  onLoad = () => {
    this.setState({
      isLoaded: true,
      hasError: false
    });
  }

  //
  // Render

  render() {
    const {
      className,
      style,
      size,
      lazy,
      overflow
    } = this.props;

    const {
      posterUrl,
      hasError,
      isLoaded
    } = this.state;

    if (hasError || !posterUrl) {
      return (
        <img
          className={className}
          style={style}
          src={posterPlaceholder}
        />
      );
    }

    if (lazy) {
      return (
        <LazyLoad
          height={size}
          offset={100}
          overflow={overflow}
          placeholder={
            <img
              className={className}
              style={style}
              src={posterPlaceholder}
            />
          }
        >
          <img
            className={className}
            style={style}
            src={posterUrl}
            onError={this.onError}
          />
        </LazyLoad>
      );
    }

    return (
      <img
        className={className}
        style={style}
        src={isLoaded ? posterUrl : posterPlaceholder}
        onError={this.onError}
        onLoad={this.onLoad}
      />
    );
  }
}

ArtistPoster.propTypes = {
  className: PropTypes.string,
  style: PropTypes.object,
  images: PropTypes.arrayOf(PropTypes.object).isRequired,
  size: PropTypes.number.isRequired,
  lazy: PropTypes.bool.isRequired,
  overflow: PropTypes.bool.isRequired
};

ArtistPoster.defaultProps = {
  size: 250,
  lazy: true,
  overflow: false
};

export default ArtistPoster;
